import React from 'react';

const CartPage = ({ cart, emptyCart, checkout, products }) => {
  
  const calculateTotalPrice = () => {
    let totalPrice = 0;
    cart.forEach(productId => {
      const product = products.find(product => product.id === productId);
      totalPrice += product.price;
    });
    return totalPrice;
  };

  return (
    <div>
      <h1>Kundvagn</h1>
      {cart.length > 0 ? (
        <div>
          <ul>
            {cart.map((productId, index) => (
              <li key={index}>{/* Här kan du visa produkterna i kundvagnen */}</li>
            ))}
          </ul>
          <p>Totalt: {calculateTotalPrice()} kr</p>
          <button onClick={checkout}>Genomför köp</button>
          <button onClick={emptyCart}>Töm kundvagnen</button>
        </div>
      ) : (
        <p>Kundvagnen är tom</p>
      )}
    </div>
  );
};

export default CartPage;
